mapper.insert:
insert into bl_blog(
    blog_id, blog_title,blog_image, blog_content, blog_type, blog_remark, blog_source
) values (
    #{blogId}, #{blogTitle}, #{blogImage}, #{blogContent}, #{blogType}, #{blogRemark}, #{blogSource}
)

mapper.update:
update bl_blog set
version = version + 1

    ,blog_title = #{blogTitle}


    ,blog_image = #{blogImage}


    ,blog_content = #{blogContent}


    ,blog_goods = #{blogGoods}


    ,blog_read = #{blogRead}


    ,blog_collection = #{blogCollection}


    ,blog_type = #{blogType}


    ,blog_remark = #{blogRemark}


    ,blog_comment = #{blogComment}


    ,blog_source = #{blogSource}

where blog_id = #{blogId}
and version = #{version}

update bl_blog set
deleted = 1
where blog_id = #{id}

mapper.select:
select blog_id, blog_title, blog_image, blog_goods, blog_read, blog_collection, blog_remark, blog_comment, blog_source, created_time, update_time,type_name from bl_blog as b inner join bl_type as t on type_id = blog_type where t.deleted = 0 and b.deleted = 0
and blog_title like CONCAT('%', #{params.blogTitle}, '%')
and type_id = #{params.typeId}
order by ${sortColumn} ${sortMethod}
limit #{index}, #{pageSize}

select count(*) from bl_blog as b inner join bl_type as t on type_id = blog_type where t.deleted = 0 and b.deleted = 0
and blog_title like CONCAT('%', #{params.blogTitle}, '%')
and type_id = #{params.typeId}
